import axios from "axios";
import React, { useState } from "react";
import "../CSS/Login.css";
import InternImg1 from "../images/intern_login_img1.png";
import Header from "../components/header";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import HttpsOutlinedIcon from "@mui/icons-material/HttpsOutlined";

export default function Login() {
  const initialValues = {
    email: "",
    password: "",
  };

  const [loginForm, setLoginForm] = useState(initialValues);

  const onChange = (e) => {
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });
  };

  const submit = () => {
    const payload = {
      email: loginForm.email,
      password: loginForm.password,
    };

    console.log(payload);
    axios
      .post(`http://localhost:8000/auth/login`, payload)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {});
  };

  return (
    <div>
      <Header />
      <div className="outer_container">
        <div className="first_layer row">
          <div className="col-2">
            <img className="img1" src={InternImg1} />
          </div>
          <div className="header1 col-6 ">For Interns</div>
        </div>
        <div className="row">
          <hr className="header_hr" />
        </div>

        <div className="second_layer">
          <p className="sign_tag"> Sign in to continue</p>
          <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text" id="basic-addon1">
                <EmailOutlinedIcon />
              </span>
            </div>
            <input
              type="text"
              className="form-control"
              placeholder="Email (required)"
              aria-label="Username"
              aria-describedby="basic-addon1"
            />
          </div>

          <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text" id="basic-addon2">
                <HttpsOutlinedIcon />
              </span>
            </div>
            <input
              type="text"
              className="form-control"
              placeholder="Password (required)"
              aria-label="Username"
              aria-describedby="basic-addon1"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

